import React from 'react';
import { MapContainer, Marker, Popup, Polygon, useMap } from 'react-leaflet';
import L from 'leaflet';
import './CartoonMap.css';

// Helper function to create SVG data URL
const createIconUrl = (svgString) => {
  return 'data:image/svg+xml;charset=utf-8,' + encodeURIComponent(svgString);
};

// Gold glittery poop emoji icon
const poopIcon = new L.DivIcon({
  html: '<div style="font-size: 32px; text-shadow: 0 0 8px rgba(255, 215, 0, 0.8);">üí©‚ú®</div>',
  className: 'custom-poop-icon',
  iconSize: [40, 40],
  iconAnchor: [20, 40],
  popupAnchor: [0, -40]
});

// Aggressive goose icon (black and gold)
const aggressiveIcon = new L.Icon({
  iconUrl: createIconUrl(`
    <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 32 32">
      <circle cx="16" cy="16" r="12" fill="#000000" stroke="#FFD700" stroke-width="2"/>
      <path d="M16 10 L16 18 M16 20 L16 22" stroke="#FFD700" stroke-width="2" stroke-linecap="round"/>
      <circle cx="16" cy="20" r="1" fill="#FFD700"/>
    </svg>
  `),
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

// Component to fit map bounds to campus
function CampusBounds({ bounds }) {
  const map = useMap();

  React.useEffect(() => {
    if (bounds) {
      map.fitBounds([
        [bounds.south, bounds.west],
        [bounds.north, bounds.east]
      ], { padding: [50, 50] });
    }
  }, [bounds, map]);

  return null;
}

// Cartoon map overlay component - draws walkways with both sides visible
function CartoonMapOverlay({ bounds }) {
  if (!bounds) return null;

  // These are approximate paths for UW campus walkways
  // Each path has parallel lines to show both sides
  const walkways = [
    // Main walkway from north to south (Ring Road) - shows both sides
    {
      center: [
        [43.4750, -80.5400],
        [43.4730, -80.5400],
        [43.4710, -80.5400],
        [43.4690, -80.5400],
        [43.4670, -80.5400],
        [43.4650, -80.5400],
        [43.4630, -80.5400]
      ],
      width: 0.0015 // offset for parallel lines
    },
    // East-west main path
    {
      center: [
        [43.4700, -80.5500],
        [43.4700, -80.5450],
        [43.4700, -80.5400],
        [43.4700, -80.5350],
        [43.4700, -80.5300]
      ],
      width: 0.0015
    },
    // Diagonal path
    {
      center: [
        [43.4750, -80.5450],
        [43.4720, -80.5420],
        [43.4690, -80.5390],
        [43.4660, -80.5360]
      ],
      width: 0.0012
    },
    // Another diagonal
    {
      center: [
        [43.4750, -80.5350],
        [43.4720, -80.5380],
        [43.4690, -80.5410],
        [43.4660, -80.5440]
      ],
      width: 0.0012
    },
    // Side paths
    {
      center: [
        [43.4720, -80.5500],
        [43.4720, -80.5450],
        [43.4720, -80.5400],
        [43.4720, -80.5350]
      ],
      width: 0.001
    },
    {
      center: [
        [43.4680, -80.5500],
        [43.4680, -80.5450],
        [43.4680, -80.5400],
        [43.4680, -80.5350]
      ],
      width: 0.001
    }
  ];

  // Function to create walkway with both sides visible
  // Creates a wider path with black borders on both sides
  const createWalkwayPath = (centerLine, width) => {
    // For north-south paths, offset longitude
    // For east-west paths, offset latitude
    // Simple approximation: create a rectangle around the center line
    const isVertical = Math.abs(centerLine[0][0] - centerLine[centerLine.length - 1][0]) > 
                      Math.abs(centerLine[0][1] - centerLine[centerLine.length - 1][1]);

    if (isVertical) {
      // Vertical path - offset longitude
      const left = centerLine.map(([lat, lng]) => [lat, lng - width]);
      const right = centerLine.map(([lat, lng]) => [lat, lng + width]);
      return [...left, ...right.reverse()];
    } else {
      // Horizontal path - offset latitude
      const top = centerLine.map(([lat, lng]) => [lat + width, lng]);
      const bottom = centerLine.map(([lat, lng]) => [lat - width, lng]);
      return [...top, ...bottom.reverse()];
    }
  };

  return (
    <>
      {/* Draw walkways with both sides visible as filled rectangles */}
      {walkways.map((walkway, idx) => {
        const walkwayPath = createWalkwayPath(walkway.center, walkway.width);
        return (
          <React.Fragment key={`walkway-${idx}`}>
            {/* Main walkway body (gold fill) */}
            <Polygon
              positions={walkwayPath}
              pathOptions={{
                color: '#000000',
                fillColor: '#FFD700',
                fillOpacity: 0.7,
                weight: 4,
                opacity: 1
              }}
            />
            {/* Center line for clarity */}
            <Polygon
              positions={walkway.center}
              pathOptions={{
                color: '#000000',
                fillColor: 'transparent',
                fillOpacity: 0,
                weight: 1,
                opacity: 0.3,
                dashArray: '8, 4'
              }}
            />
          </React.Fragment>
        );
      })}
    </>
  );
}

function CartoonMap({ reports, habitats, campusBounds, onReportClick }) {
  const [showHabitats, setShowHabitats] = React.useState(true);

  // University of Waterloo center coordinates
  const uwCenter = [43.4700, -80.5400];

  return (
    <div className="cartoon-map-view">
      <div className="map-controls">
        <label className="toggle-habitats">
          <input
            type="checkbox"
            checked={showHabitats}
            onChange={(e) => setShowHabitats(e.target.checked)}
          />
          Show Geese Habitats
        </label>
      </div>

      <MapContainer
        center={uwCenter}
        zoom={16}
        style={{ height: '100%', width: '100%', background: '#f5f5f5' }}
        scrollWheelZoom={true}
        zoomControl={true}
      >
        {/* Custom cartoon background - no tile layer, just our custom drawing */}
        {campusBounds && <CampusBounds bounds={campusBounds} />}

        {/* Campus boundary */}
        {campusBounds && (
          <Polygon
            positions={[
              [campusBounds.south, campusBounds.west],
              [campusBounds.north, campusBounds.west],
              [campusBounds.north, campusBounds.east],
              [campusBounds.south, campusBounds.east],
            ]}
            pathOptions={{
              color: '#000000',
              fillColor: '#FFFFFF',
              fillOpacity: 0.9,
              weight: 3,
              dashArray: '10, 5'
            }}
          />
        )}

        {/* Draw cartoon walkways */}
        <CartoonMapOverlay bounds={campusBounds} />

        {/* Geese habitats */}
        {showHabitats && habitats.map((habitat) => (
          <Polygon
            key={habitat.id}
            positions={habitat.coordinates}
            pathOptions={{
              color: '#FFD700',
              fillColor: '#FFD700',
              fillOpacity: 0.2,
              weight: 2,
              dashArray: '5, 5'
            }}
          >
            <Popup>
              <div>
                <strong>{habitat.name}</strong>
                <br />
                <small>Geese Habitat ({habitat.type})</small>
              </div>
            </Popup>
          </Polygon>
        ))}

        {/* Report markers */}
        {reports.map((report) => (
          <Marker
            key={report.id}
            position={[report.latitude, report.longitude]}
            icon={report.type === 'aggressive' ? aggressiveIcon : poopIcon}
          >
            <Popup>
              <div className="custom-popup">
                <h3>{report.type === 'aggressive' ? '‚ö†Ô∏è Aggressive Goose' : 'üí©‚ú® Poop Report'}</h3>
                {report.description && <p>{report.description}</p>}
                <p><strong>Severity:</strong> {report.severity}</p>
                <p><small>{new Date(report.timestamp).toLocaleString()}</small></p>
                {report.imageUrl && (
                  <img
                    src={`http://localhost:5000${report.imageUrl}`}
                    alt="Proof"
                    style={{ maxWidth: '200px', maxHeight: '200px', marginTop: '0.5rem', borderRadius: '8px' }}
                  />
                )}
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}

export default CartoonMap;
