import React, { useEffect, useRef, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle, Polygon, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import CartoonMap from './CartoonMap';
import './MapView.css';

// Fix for default marker icons in React-Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
});

// Helper function to create SVG data URL
const createIconUrl = (svgString) => {
  return 'data:image/svg+xml;charset=utf-8,' + encodeURIComponent(svgString);
};

// Custom icons
const poopIcon = new L.Icon({
  iconUrl: createIconUrl(`
    <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 32 32">
      <circle cx="16" cy="16" r="12" fill="#8B4513" stroke="#654321" stroke-width="2"/>
      <circle cx="12" cy="14" r="2" fill="#654321"/>
      <circle cx="20" cy="14" r="2" fill="#654321"/>
    </svg>
  `),
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

const aggressiveIcon = new L.Icon({
  iconUrl: createIconUrl(`
    <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 32 32">
      <circle cx="16" cy="16" r="12" fill="#FF4444" stroke="#CC0000" stroke-width="2"/>
      <path d="M16 10 L16 18 M16 20 L16 22" stroke="white" stroke-width="2" stroke-linecap="round"/>
      <circle cx="16" cy="20" r="1" fill="white"/>
    </svg>
  `),
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

// Component to fit map bounds to campus
function CampusBounds({ bounds }) {
  const map = useMap();

  useEffect(() => {
    if (bounds) {
      map.fitBounds([
        [bounds.south, bounds.west],
        [bounds.north, bounds.east]
      ], { padding: [50, 50] });
    }
  }, [bounds, map]);

  return null;
}

function MapView({ reports, habitats, campusBounds, onReportClick }) {
  // Use the cartoon map instead
  return (
    <CartoonMap
      reports={reports}
      habitats={habitats}
      campusBounds={campusBounds}
      onReportClick={onReportClick}
    />
  );
}

export default MapView;
