import React, { useEffect, useRef, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle, Polygon, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './MapView.css';

// Fix for default marker icons in React-Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
});

// Custom icons
const poopIcon = new L.Icon({
  iconUrl: 'data:image/svg+xml;base64,' + btoa(`
    <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 32 32">
      <circle cx="16" cy="16" r="12" fill="#8B4513" stroke="#654321" stroke-width="2"/>
      <circle cx="12" cy="14" r="2" fill="#654321"/>
      <circle cx="20" cy="14" r="2" fill="#654321"/>
    </svg>
  `),
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

const aggressiveIcon = new L.Icon({
  iconUrl: 'data:image/svg+xml;base64,' + btoa(`
    <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 32 32">
      <circle cx="16" cy="16" r="12" fill="#FF4444" stroke="#CC0000" stroke-width="2"/>
      <text x="16" y="22" font-size="18" text-anchor="middle" fill="white">‚ö†</text>
    </svg>
  `),
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

// Component to fit map bounds to campus
function CampusBounds({ bounds }) {
  const map = useMap();

  useEffect(() => {
    if (bounds) {
      map.fitBounds([
        [bounds.south, bounds.west],
        [bounds.north, bounds.east]
      ], { padding: [50, 50] });
    }
  }, [bounds, map]);

  return null;
}

function MapView({ reports, habitats, campusBounds, onReportClick }) {
  const [showHabitats, setShowHabitats] = useState(true);

  // University of Waterloo center coordinates
  const uwCenter = [43.4700, -80.5400];

  return (
    <div className="map-view">
      <div className="map-controls">
        <label className="toggle-habitats">
          <input
            type="checkbox"
            checked={showHabitats}
            onChange={(e) => setShowHabitats(e.target.checked)}
          />
          Show Geese Habitats
        </label>
      </div>

      <MapContainer
        center={uwCenter}
        zoom={16}
        style={{ height: '100%', width: '100%' }}
        scrollWheelZoom={true}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {campusBounds && <CampusBounds bounds={campusBounds} />}

        {/* Campus boundary overlay */}
        {campusBounds && (
          <Polygon
            positions={[
              [campusBounds.south, campusBounds.west],
              [campusBounds.north, campusBounds.west],
              [campusBounds.north, campusBounds.east],
              [campusBounds.south, campusBounds.east],
            ]}
            color="#667eea"
            fillColor="#667eea"
            fillOpacity={0.1}
            weight={2}
            dashArray="5, 5"
          />
        )}

        {/* Geese habitats */}
        {showHabitats && habitats.map((habitat) => (
          <Polygon
            key={habitat.id}
            positions={habitat.coordinates}
            color="#48bb78"
            fillColor="#48bb78"
            fillOpacity={0.3}
            weight={2}
          >
            <Popup>
              <div>
                <strong>{habitat.name}</strong>
                <br />
                <small>Geese Habitat ({habitat.type})</small>
              </div>
            </Popup>
          </Polygon>
        ))}

        {/* Report markers */}
        {reports.map((report) => (
          <Marker
            key={report.id}
            position={[report.latitude, report.longitude]}
            icon={report.type === 'aggressive' ? aggressiveIcon : poopIcon}
          >
            <Popup>
              <div className="custom-popup">
                <h3>{report.type === 'aggressive' ? '‚ö†Ô∏è Aggressive Goose' : 'üí© Poop Report'}</h3>
                {report.description && <p>{report.description}</p>}
                <p><strong>Severity:</strong> {report.severity}</p>
                <p><small>{new Date(report.timestamp).toLocaleString()}</small></p>
                {report.imageUrl && (
                  <img
                    src={`http://localhost:5000${report.imageUrl}`}
                    alt="Proof"
                    style={{ maxWidth: '200px', maxHeight: '200px', marginTop: '0.5rem', borderRadius: '8px' }}
                  />
                )}
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}

export default MapView;
